table.ambox{margin:0 10%;border:1px solid #a2a9b1;border-left:10px solid #36c;background-color:#fbfbfb;box-sizing:border-box}table.ambox + table.ambox{margin-top:-1px}.ambox th.mbox-text,.ambox td.mbox-text{padding:0.25em 0.5em}.ambox td.mbox-image{padding:2px 0 2px 0.5em}.ambox td.mbox-imageright{padding:2px 0.5em 2px 0}table.ambox-notice{border-left:10px solid #36c}table.ambox-speedy{border-left:10px solid #b32424;background-color:#fee7e6}table.ambox-delete{border-left:10px solid #b32424}table.ambox-content{border-left:10px solid #f28500}table.ambox-style{border-left:10px solid #fc3}table.ambox-move{border-left:10px solid #9932cc}table.ambox-protection{border-left:10px solid #a2a9b1}table.ombox-move{border:1px solid #9932cc}table.ombox-protection{border:2px solid #a2a9b1}th.mbox-text,td.mbox-text{border:none;padding:0.25em 0.9em;width:100%}td.mbox-image{border:none;padding:2px 0 2px 0.9em;text-align:center}td.mbox-imageright{border:none;padding:2px 0.9em 2px 0;text-align:center}td.mbox-empty-cell{border:none;padding:0px;width:1px}