package codex.xbit.api.server.trader.env.single.level1;
//-------------------------------------------------------------------------------------
import java.io.*;
import java.util.*;


import codex.common.utils.*;
import codex.xbit.api.common.configs.*;

import codex.xbit.api.server.trader.*;
import codex.xbit.api.server.trader.core.components.*;
import codex.xbit.api.server.trader.env.single.level1.repositories.*;

//-------------------------------------------------------------------------------------
public class Level1 extends AbstractLevel {

    private Repositories repositories;

//-------------------------------------------------------------------------------------
    public Level1(Debug _debug, Resolver _resolver) {
    	super(_debug, _resolver, "L1");
        repositories = new Repositories(debug, _resolver);
    }

//-------------------------------------------------------------------------------------
    public Repositories getRepositories() {
    	return repositories;
	}

//-------------------------------------------------------------------------------------
//-------------------------------------------------------------------------------------
//-------------------------------------------------------------------------------------
//-------------------------------------------------------------------------------------
//-------------------------------------------------------------------------------------
    public void load() {
        repositories.load();
	}

//-------------------------------------------------------------------------------------
    public void save(){
    	if(repositories != null){
        	repositories.save();
        }
    }

//-------------------------------------------------------------------------------------
//-------------------------------------------------------------------------------------
//-------------------------------------------------------------------------------------
//-------------------------------------------------------------------------------------
}
//-------------------------------------------------------------------------------------
