	public synchronized IndicatorX getIndicator(CurrencyPair _currencyPair, List<String> _indicator){
//		String _repositoryName, 
    	Key _key = new Key(_currencyPair, _indicator);
		IndicatorX _indicatorX = getEntity(_key);
		if(_indicatorX == null){
			_indicatorX = new IndicatorX(debug, resolver, _currencyPair, _indicator);
//			_indicatorX.load();
			addEntity(_key, _indicatorX);
		}
		return _indicatorX;
	}

