import java.io.*;
import java.util.*;

import codex.common.utils.*;
import codex.xbit.api.common.configs.*;

import codex.xbit.api.server.trader.*;
import codex.xbit.api.server.trader.core.components.*;
import codex.xbit.api.server.trader.env.single.level2.indicators.*;
import codex.xbit.api.server.trader.env.single.level3.strategies.*;

//-------------------------------------------------------------------------------------
public class Level3 extends AbstractLevel {

    private Strategies strategies;

//-------------------------------------------------------------------------------------
    public Level3(Debug _debug, Resolver _resolver) {
    	super(_debug, _resolver, "L3");
        strategies = new Strategies(debug, _resolver);
    }

//-------------------------------------------------------------------------------------
//-------------------------------------------------------------------------------------
//-------------------------------------------------------------------------------------
//-------------------------------------------------------------------------------------
//-------------------------------------------------------------------------------------
    public Strategies getStrategies() {
        return strategies;
	}

//-------------------------------------------------------------------------------------
    public void changeStrategy(String[] _args) {
    	strategies.changeStrategy(_args);
	}

//-------------------------------------------------------------------------------------
//-------------------------------------------------------------------------------------
//-------------------------------------------------------------------------------------
//-------------------------------------------------------------------------------------
//-------------------------------------------------------------------------------------
    public void load() {
        strategies.load();
	}

//-------------------------------------------------------------------------------------
    public void save(){
        strategies.save();
    }

//-------------------------------------------------------------------------------------
//-------------------------------------------------------------------------------------
//-------------------------------------------------------------------------------------
//-------------------------------------------------------------------------------------
}
