import java.io.*;
import java.util.*;

import codex.common.utils.*;
import codex.xbit.api.common.configs.*;
import codex.xbit.api.common.packets.*;

import codex.xbit.api.server.trader.*;
import codex.xbit.api.server.trader.core.components.*;
import codex.xbit.api.server.trader.env.single.level5.signals.*;

//-------------------------------------------------------------------------------------
public class Level5 extends AbstractLevel {

    private Signals signals;

//-------------------------------------------------------------------------------------
    public Level5(Debug _debug, Resolver _resolver) {
    	super(_debug, _resolver, "L5");
        signals = new Signals(debug, _resolver);
    }

//-------------------------------------------------------------------------------------
//-------------------------------------------------------------------------------------
//-------------------------------------------------------------------------------------
//-------------------------------------------------------------------------------------
//-------------------------------------------------------------------------------------
//-------------------------------------------------------------------------------------
    public Signals getSignals() {
        return signals;
	}

//-------------------------------------------------------------------------------------
    public void sendSignal(String[] _args) {
    	signals.sendSignal(_args);
	}

//-------------------------------------------------------------------------------------
	public Object getInfo(int _netCommandParameter){
		return signals.getInfo(_netCommandParameter);
	}

//-------------------------------------------------------------------------------------
//-------------------------------------------------------------------------------------
//-------------------------------------------------------------------------------------
//-------------------------------------------------------------------------------------
//-------------------------------------------------------------------------------------
    public void load() {
        signals.load();
	}

//-------------------------------------------------------------------------------------
    public void save(){
        signals.save();
    }

//-------------------------------------------------------------------------------------
//-------------------------------------------------------------------------------------
//-------------------------------------------------------------------------------------
//-------------------------------------------------------------------------------------
}
