//-------------------------------------------------------------------------------------
public class Level3 extends AbstractLevel {

    private Agents agents;
    private Mixers mixers;

//-------------------------------------------------------------------------------------
    public Level3(Debug _debug, Resolver _resolver) {
    	super(_debug, _resolver, "L3");
        agents = new Agents(debug, _resolver);
        mixers = new Mixers(debug, _resolver);
    }

//-------------------------------------------------------------------------------------
//-------------------------------------------------------------------------------------
//-------------------------------------------------------------------------------------
//-------------------------------------------------------------------------------------
//-------------------------------------------------------------------------------------
//-------------------------------------------------------------------------------------
	public Agents getAgents(){
		return agents;
	}

//-------------------------------------------------------------------------------------
	public Mixers getMixers(){
		return mixers;
	}

//-------------------------------------------------------------------------------------
	public Object getInfo(int _netCommandParameter, String _tactic){
//		return tactics.getInfo(_netCommandParameter, _tactic);
		return null;		
	}

//-------------------------------------------------------------------------------------
//-------------------------------------------------------------------------------------
//-------------------------------------------------------------------------------------
//-------------------------------------------------------------------------------------
//-------------------------------------------------------------------------------------
//-------------------------------------------------------------------------------------
    public void load() {
        agents.load();
        mixers.load();
	}

//-------------------------------------------------------------------------------------
    public void save(){
//    	debug.outln(Debug.IMPORTANT3, "Level2.save...");
    	if(agents != null){
        	agents.save();
        }
    	if(mixers != null){
        	mixers.save();
        }
    }

//-------------------------------------------------------------------------------------
//-------------------------------------------------------------------------------------
//-------------------------------------------------------------------------------------
//-------------------------------------------------------------------------------------
}
